import React, { useState, useEffect } from "react";
import Login from "./Login";
import Register from "./Register";
import MainApp from "./MainApp";

const App = () => {
  const [currentView, setCurrentView] = useState("app"); // "login", "register", "app"
  const [user, setUser] = useState({
    name: "Guest",
    email: "guest@linkmini.com",
  });

  // Check if user is logged in on app load
  useEffect(() => {
    const savedUser = localStorage.getItem("currentUser");
    if (savedUser) {
      setUser(JSON.parse(savedUser));
      setCurrentView("app");
    }
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem("currentUser", JSON.stringify(userData));
    setCurrentView("app");
  };

  const handleRegister = (userData) => {
    setUser(userData);
    localStorage.setItem("currentUser", JSON.stringify(userData));
    setCurrentView("app");
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem("currentUser");
    setCurrentView("login");
  };

  const switchToLogin = () => {
    setCurrentView("login");
  };

  const switchToRegister = () => {
    setCurrentView("register");
  };

  if (currentView === "app") {
    return (
      <MainApp
        user={user}
        onLogout={handleLogout}
        onShowLogin={switchToLogin}
        onShowRegister={switchToRegister}
      />
    );
  }

  if (currentView === "register") {
    return (
      <Register onRegister={handleRegister} onSwitchToLogin={switchToLogin} />
    );
  }

  return <Login onLogin={handleLogin} onSwitchToRegister={switchToRegister} />;
};

export default App;
