import React, { useState, useEffect, useRef, useCallback } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import QRCode from "qrcode";

const MainApp = ({ user, onLogout, onShowLogin, onShowRegister }) => {
  const [url, setUrl] = useState("");
  const [customAlias, setCustomAlias] = useState("");
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");
  const [recentLinks, setRecentLinks] = useState([]);
  const [copied, setCopied] = useState(false);
  const [isVisible, setIsVisible] = useState({});
  const [activeTab, setActiveTab] = useState("shortener");
  const [qrCodeUrl, setQrCodeUrl] = useState("");
  const [qrCodeImage, setQrCodeImage] = useState("");
  const [qrOptions, setQrOptions] = useState({
    errorCorrectionLevel: "M",
    type: "image/png",
    quality: 0.92,
    margin: 1,
    color: {
      dark: "#000000",
      light: "#FFFFFF",
    },
    width: 256,
  });
  const [qrLogo, setQrLogo] = useState(null);
  const [exportFormat, setExportFormat] = useState("PNG");
  const [exportSize, setExportSize] = useState("256px");
  const [socialTab, setSocialTab] = useState("URL");
  const [socialData, setSocialData] = useState({
    URL: "",
    Facebook: "",
    Pinterest: "",
    LinkedIn: "",
    WhatsApp: "",
    Email: "",
    Telephone: "",
    SMS: "",
  });

  const observerRef = useRef();
  const statsRef = useRef();
  const pricingRef = useRef();
  const featuresRef = useRef();

  // Intersection Observer for scroll animations
  useEffect(() => {
    observerRef.current = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsVisible((prev) => ({
              ...prev,
              [entry.target.id]: true,
            }));
          }
        });
      },
      { threshold: 0.1 },
    );

    const elements = [
      statsRef.current,
      pricingRef.current,
      featuresRef.current,
    ];
    elements.forEach((el) => {
      if (el) observerRef.current.observe(el);
    });

    return () => {
      if (observerRef.current) {
        observerRef.current.disconnect();
      }
    };
  }, []);

  // Counter animation for statistics
  const useCountUp = (end, duration = 2000) => {
    const [count, setCount] = useState(0);
    const [hasStarted, setHasStarted] = useState(false);

    useEffect(() => {
      if (isVisible.stats && !hasStarted) {
        setHasStarted(true);
        let startTimestamp = null;
        const step = (timestamp) => {
          if (!startTimestamp) startTimestamp = timestamp;
          const progress = Math.min((timestamp - startTimestamp) / duration, 1);
          setCount(Math.floor(progress * end));
          if (progress < 1) {
            window.requestAnimationFrame(step);
          }
        };
        window.requestAnimationFrame(step);
      }
    }, [hasStarted, end, duration]);

    return count;
  };



  // Load recent links from localStorage
  useEffect(() => {
    const saved = localStorage.getItem("recentLinks");
    if (saved) {
      setRecentLinks(JSON.parse(saved));
    }
  }, []);

  // Save to localStorage
  const saveToRecent = (originalUrl, shortUrl) => {
    const newLink = {
      id: Date.now(),
      originalUrl,
      shortUrl,
      timestamp: new Date().toISOString(),
    };

    const updated = [
      newLink,
      ...recentLinks.filter((link) => link.originalUrl !== originalUrl),
    ].slice(0, 10);
    setRecentLinks(updated);
    localStorage.setItem("recentLinks", JSON.stringify(updated));
  };

  // Validate URL
  const isValidUrl = (string) => {
    try {
      const url = new URL(string);
      return url.protocol === "http:" || url.protocol === "https:";
    } catch (_) {
      return false;
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setResult(null);

    if (!url.trim()) {
      setError("Please enter a URL");
      return;
    }

    if (!isValidUrl(url.trim())) {
      setError("Please enter a valid URL (including http:// or https://)");
      return;
    }

    setLoading(true);

    const payload = {
      long_url: url.trim(),
    };

    if (customAlias.trim()) {
      payload.custom_code = customAlias.trim();
    }

    try {
      const response = await fetch("https://short.ginit.in/api.php", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(payload),
        mode: "cors",
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      if (data && data.error) {
        throw new Error(data.error);
      }

      if (data && data.short_url) {
        setResult({
          originalUrl: url.trim(),
          shortUrl: data.short_url,
          shortCode: data.short_code || "",
          qrCodeUrl: data.qr_code_url || "",
        });
        saveToRecent(url.trim(), data.short_url);
        toast.success("🎉 URL shortened successfully!", {
          position: "bottom-right",
          autoClose: 3000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
        });
      } else {
        throw new Error("Invalid response format from API");
      }
    } catch (err) {
      console.error("API error:", err);
      let errorMessage = "Failed to shorten URL. Please try again.";

      if (err.name === "TypeError" && err.message.includes("Failed to fetch")) {
        errorMessage = "Unable to connect to the shortening service. Please check your internet connection.";
      } else if (err.message.includes("CORS")) {
        errorMessage = "Network error. Please try again later.";
      } else if (err.message.includes("400")) {
        errorMessage = "Bad request. Please check your URL format.";
      } else if (err.message.includes("409")) {
        errorMessage = "This custom alias is already taken";
      } else if (err.message.includes("500")) {
        errorMessage = "Server error. Please try again later.";
      } else if (err.message.includes("404")) {
        errorMessage = "Service temporarily unavailable. Please try again later.";
      } else if (err.message) {
        errorMessage = err.message;
      }

      setError(errorMessage);
      toast.error(`❌ ${errorMessage}`, {
        position: "bottom-right",
        autoClose: 5000,
      });
    } finally {
      setLoading(false);
    }
  };

  // Generate QR code
  const generateQRCode = useCallback(async (text) => {
    try {
      if (!text.trim()) {
        setQrCodeImage("");
        return;
      }

      const qrCodeDataURL = await QRCode.toDataURL(text, qrOptions);
      setQrCodeImage(qrCodeDataURL);
    } catch (error) {
      console.error("Error generating QR code:", error);
      toast.error("❌ Failed to generate QR code", {
        position: "bottom-right",
        autoClose: 3000,
      });
    }
  }, [qrOptions]);

  // Handle logo upload
  const handleLogoUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      if (file.size > 2 * 1024 * 1024) {
        toast.error("❌ Logo file must be less than 2MB", {
          position: "bottom-right",
          autoClose: 3000,
        });
        return;
      }

      const reader = new FileReader();
      reader.onload = (e) => {
        setQrLogo(e.target.result);
        toast.success("✅ Logo uploaded successfully!", {
          position: "bottom-right",
          autoClose: 2000,
        });
      };
      reader.readAsDataURL(file);
    }
  };

  // Handle social tab data change
  const handleSocialDataChange = (value) => {
    setSocialData((prev) => ({
      ...prev,
      [socialTab]: value,
    }));

    if (socialTab === "URL") {
      setQrCodeUrl(value);
    } else if (socialTab === "Email") {
      setQrCodeUrl(value ? `mailto:${value}` : "");
    } else if (socialTab === "Telephone") {
      setQrCodeUrl(value ? `tel:${value}` : "");
    } else if (socialTab === "SMS") {
      setQrCodeUrl(value ? `sms:${value}` : "");
    } else if (socialTab === "WhatsApp") {
      setQrCodeUrl(value ? `https://wa.me/${value}` : "");
    } else {
      setQrCodeUrl(value);
    }
  };

  // Real-time QR code generation
  useEffect(() => {
    if (activeTab === "qr-generator" && qrCodeUrl) {
      generateQRCode(qrCodeUrl);
    }
  }, [qrCodeUrl, qrOptions, activeTab, generateQRCode]);

  // Download QR code
  const downloadQRCode = () => {
    if (!qrCodeImage) return;

    const link = document.createElement("a");
    link.download = `qr-code-${exportSize}.${exportFormat.toLowerCase()}`;
    link.href = qrCodeImage;
    link.click();

    toast.success(`📥 QR code downloaded as ${exportFormat}!`, {
      position: "bottom-right",
      autoClose: 2000,
    });
  };

  // Share QR code on social media
  const shareQRCode = (platform) => {
    const text = `Check out this QR code generated with Linkmini!`;
    const url = window.location.href;

    let shareUrl = "";

    switch (platform) {
      case "facebook":
        shareUrl = `https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(url)}`;
        break;
      case "twitter":
        shareUrl = `https://twitter.com/intent/tweet?text=${encodeURIComponent(text)}&url=${encodeURIComponent(url)}`;
        break;
      case "linkedin":
        shareUrl = `https://www.linkedin.com/sharing/share-offsite/?url=${encodeURIComponent(url)}`;
        break;
      case "pinterest":
        shareUrl = `https://pinterest.com/pin/create/button/?url=${encodeURIComponent(url)}&description=${encodeURIComponent(text)}`;
        break;
      case "whatsapp":
        shareUrl = `https://wa.me/?text=${encodeURIComponent(text + " " + url)}`;
        break;
      default:
        return;
    }

    window.open(shareUrl, "_blank", "width=600,height=400");

    toast.success(`📱 Shared on ${platform}!`, {
      position: "bottom-right",
      autoClose: 2000,
    });
  };

  // Copy to clipboard with enhanced feedback
  const copyToClipboard = async (text) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
      toast.success("📋 Copied to clipboard!", {
        position: "bottom-right",
        autoClose: 2000,
      });
    } catch (err) {
      const textArea = document.createElement("textarea");
      textArea.value = text;
      document.body.appendChild(textArea);
      textArea.select();
      document.execCommand("copy");
      document.body.removeChild(textArea);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
      toast.success("📋 Copied to clipboard!");
    }
  };

  return (
    <div className="min-h-screen bg-white">
      {/* Enhanced Header */}
      <header className="header-glassmorphism sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 py-4 flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-gradient-to-br from-blue-500 via-purple-500 to-indigo-600 rounded-xl flex items-center justify-center shadow-lg">
              <span className="text-white font-bold text-lg">L</span>
            </div>
            <div>
              <span className="font-bold text-gradient text-2xl bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                Linkmini
              </span>
            </div>
          </div>

          <div className="flex items-center space-x-3">
            <button
              onClick={() => setActiveTab("shortener")}
              className={`px-6 py-3 text-sm rounded-xl transition-all duration-300 transform hover:scale-105 shadow-lg font-medium ${
                activeTab === "shortener"
                  ? "bg-gradient-to-r from-blue-600 to-blue-700 text-white shadow-blue-500/30"
                  : "bg-gradient-to-r from-blue-500 to-blue-600 text-white hover:from-blue-600 hover:to-blue-700 shadow-blue-400/20"
              }`}
            >
              🔗 URL Shortener
            </button>
            <button
              onClick={() => setActiveTab("qr-generator")}
              className={`px-6 py-3 text-sm rounded-xl transition-all duration-300 transform hover:scale-105 shadow-lg font-medium ${
                activeTab === "qr-generator"
                  ? "bg-gradient-to-r from-purple-600 to-purple-700 text-white shadow-purple-500/30"
                  : "bg-gradient-to-r from-purple-500 to-purple-600 text-white hover:from-purple-600 hover:to-purple-700 shadow-purple-400/20"
              }`}
            >
              📱 QR Generator
            </button>
          </div>

          <div className="flex items-center space-x-4">
            {user.email === "guest@linkmini.com" ? (
              <>
                <button
                  onClick={onShowLogin}
                  className="text-sm text-gray-600 hover:text-gray-800 transition-colors font-medium"
                >
                  Sign in
                </button>
                <button
                  onClick={onShowRegister}
                  className="btn-primary text-sm px-6 py-3 rounded-xl"
                >
                  Sign up for free
                </button>
              </>
            ) : (
              <>
                <span className="text-sm text-gray-600 font-medium">
                  Welcome, {user.name}!
                </span>
                <button
                  onClick={onLogout}
                  className="btn-secondary text-sm px-6 py-3 rounded-xl"
                >
                  Logout
                </button>
              </>
            )}
          </div>
        </div>
      </header>

      {/* Beautiful Enhanced Hero Section */}
      <section className="relative min-h-[80vh] overflow-hidden">
        {/* Background with animated gradient */}
        <div className="absolute inset-0 bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
          <div className="absolute inset-0 bg-gradient-to-r from-blue-500/10 via-purple-500/10 to-indigo-500/10 animate-pulse"></div>
        </div>

        {/* Floating shapes */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
          <div className="absolute top-20 left-20 w-32 h-32 bg-blue-400 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-blob"></div>
          <div className="absolute top-40 right-20 w-32 h-32 bg-purple-400 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-blob animation-delay-2000"></div>
          <div className="absolute bottom-20 left-40 w-32 h-32 bg-indigo-400 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-blob animation-delay-4000"></div>
        </div>

        <div className="relative max-w-7xl mx-auto px-4 py-20 text-center">
          {activeTab === "shortener" ? (
            <>
              <div className="mb-8">
                <h1 className="text-6xl md:text-7xl font-bold mb-6 leading-tight">
                  <span className="bg-gradient-to-r from-blue-600 via-purple-600 to-indigo-600 bg-clip-text text-transparent">
                    Shorten Links
                  </span>
                  <br />
                  <span className="text-gray-800">Like a Pro</span>
                </h1>
                <p className="text-xl md:text-2xl text-gray-600 mb-12 max-w-4xl mx-auto leading-relaxed">
                  Transform your long, ugly URLs into beautiful, memorable links that are perfect for sharing anywhere
                </p>
              </div>
            </>
          ) : (
            <>
              <div className="mb-8">
                <h1 className="text-6xl md:text-7xl font-bold mb-6 leading-tight">
                  <span className="bg-gradient-to-r from-purple-600 via-indigo-600 to-blue-600 bg-clip-text text-transparent">
                    QR Codes
                  </span>
                  <br />
                  <span className="text-gray-800">Made Simple</span>
                </h1>
                <p className="text-xl md:text-2xl text-gray-600 mb-12 max-w-4xl mx-auto leading-relaxed">
                  Create stunning, customizable QR codes with real-time preview and high-quality downloads
                </p>
              </div>
            </>
          )}

          {/* Enhanced Card */}
          <div className="max-w-4xl mx-auto">
            <div className="bg-white/80 backdrop-blur-xl rounded-3xl shadow-2xl border border-white/20 p-8 md:p-12">
              {activeTab === "shortener" ? (
                <>
                  <form onSubmit={handleSubmit} className="space-y-8">
                    <div className="flex flex-col md:flex-row gap-4">
                      <div className="flex-1">
                        <input
                          type="text"
                          value={url}
                          onChange={(e) => setUrl(e.target.value)}
                          placeholder="🔗 Paste your long URL here..."
                          className={`w-full px-6 py-4 text-lg border-2 rounded-2xl focus:ring-4 focus:ring-blue-500/20 focus:border-blue-500 transition-all ${
                            error ? "border-red-300 bg-red-50" : "border-gray-200 bg-gray-50"
                          }`}
                          disabled={loading}
                        />
                      </div>
                      <button
                        type="submit"
                        disabled={loading || !url.trim()}
                        className="btn-primary px-8 py-4 text-lg rounded-2xl font-semibold whitespace-nowrap disabled:opacity-50 disabled:cursor-not-allowed transform hover:scale-105 transition-all shadow-xl"
                      >
                        {loading ? (
                          <div className="flex items-center justify-center space-x-2">
                            <div className="loading-spinner"></div>
                            <span>Shortening...</span>
                          </div>
                        ) : (
                          <div className="flex items-center justify-center space-x-2">
                            <span>✨ Shorten URL</span>
                            <span>→</span>
                          </div>
                        )}
                      </button>
                    </div>

                    <input
                      type="text"
                      value={customAlias}
                      onChange={(e) => setCustomAlias(e.target.value)}
                      placeholder="🎯 Custom alias (optional)"
                      className="w-full px-6 py-4 text-lg border-2 border-gray-200 bg-gray-50 rounded-2xl focus:ring-4 focus:ring-blue-500/20 focus:border-blue-500 transition-all"
                      disabled={loading}
                    />

                    {error && (
                      <div className="p-4 bg-red-100 border border-red-300 rounded-2xl text-red-700">
                        ❌ {error}
                      </div>
                    )}
                  </form>

                  {result && (
                    <div className="mt-8 p-6 bg-gradient-to-r from-green-50 to-emerald-50 border-2 border-green-200 rounded-2xl">
                      <h3 className="text-xl font-semibold mb-4 text-gray-800 flex items-center">
                        <div className="w-6 h-6 bg-green-500 rounded-full flex items-center justify-center mr-3">
                          <span className="text-white text-sm">✓</span>
                        </div>
                        Your shortened URL is ready!
                      </h3>
                      <div className="flex items-center bg-white rounded-xl p-4 border-2 border-green-300 shadow-sm">
                        <span className="flex-1 text-blue-600 font-mono text-left break-all text-lg">
                          {result.shortUrl}
                        </span>
                        <button
                          onClick={() => copyToClipboard(result.shortUrl)}
                          className={`copy-btn ml-4 px-4 py-2 rounded-xl font-medium transition-all ${
                            copied 
                              ? "bg-green-500 text-white" 
                              : "bg-blue-500 text-white hover:bg-blue-600"
                          }`}
                        >
                          {copied ? "✅ Copied!" : "📋 Copy"}
                        </button>
                      </div>
                      <p className="text-sm text-gray-600 mt-3">
                        <strong>Original:</strong> {result.originalUrl}
                      </p>
                      <div className="mt-6 pt-4 border-t border-gray-200 flex gap-3">
                        <button
                          onClick={() => {
                            setSocialData((prev) => ({
                              ...prev,
                              URL: result.shortUrl,
                            }));
                            setQrCodeUrl(result.shortUrl);
                            setActiveTab("qr-generator");
                          }}
                          className="btn-secondary px-6 py-3 rounded-xl"
                        >
                          📱 Generate QR Code
                        </button>

                        <button
                          onClick={() => window.open(result.shortUrl, '_blank')}
                          className="btn-secondary px-6 py-3 rounded-xl"
                        >
                          🔗 Test Link
                        </button>
                      </div>
                    </div>
                  )}
                </>
              ) : (
                <>
                  {/* QR Code Generator */}
                  <div className="space-y-8">
                    {/* Social Tabs */}
                    <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-2xl p-6 border border-blue-100">
                      <div className="flex flex-wrap gap-3 mb-6">
                        {[
                          "URL",
                          "Facebook", 
                          "Pinterest",
                          "LinkedIn",
                          "WhatsApp",
                          "Email",
                          "Telephone",
                          "SMS",
                        ].map((tab) => (
                          <button
                            key={tab}
                            onClick={() => setSocialTab(tab)}
                            className={`px-4 py-2 rounded-xl text-sm font-medium transition-all transform hover:scale-105 ${
                              socialTab === tab
                                ? "bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow-lg"
                                : "bg-white text-gray-600 hover:bg-gray-50 shadow-sm"
                            }`}
                          >
                            {tab}
                          </button>
                        ))}
                      </div>

                      <div className="flex items-center gap-4">
                        <div className="flex-1">
                          <input
                            type="text"
                            value={socialData[socialTab]}
                            onChange={(e) =>
                              handleSocialDataChange(e.target.value)
                            }
                            placeholder={
                              socialTab === "URL"
                                ? "Paste a URL to generate QR code"
                                : socialTab === "Email"
                                  ? "Enter email address"
                                  : socialTab === "Telephone"
                                    ? "Enter phone number"
                                    : socialTab === "SMS"
                                      ? "Enter SMS number"
                                      : socialTab === "WhatsApp"
                                        ? "Enter WhatsApp number"
                                        : `Enter ${socialTab} content`
                            }
                            className="w-full px-6 py-4 border-2 border-blue-200 rounded-xl focus:ring-4 focus:ring-blue-500/20 focus:border-blue-500 bg-white text-gray-700 text-lg"
                          />
                        </div>
                      </div>
                    </div>

                    <div className="grid lg:grid-cols-3 gap-8">
                      {/* Customization Panel */}
                      <div className="lg:col-span-1 space-y-6">
                        <div className="bg-white rounded-2xl border border-gray-200 p-6 shadow-sm">
                          <h3 className="text-lg font-semibold mb-4 text-gray-800">
                            Customize QR Code
                          </h3>

                          {/* Color Controls */}
                          <div className="space-y-4 mb-6">
                            <div>
                              <label className="block text-sm font-medium text-gray-700 mb-2">
                                Background Color
                              </label>
                              <div className="flex items-center space-x-3">
                                <input
                                  type="color"
                                  value={qrOptions.color.light}
                                  onChange={(e) =>
                                    setQrOptions((prev) => ({
                                      ...prev,
                                      color: {
                                        ...prev.color,
                                        light: e.target.value,
                                      },
                                    }))
                                  }
                                  className="w-12 h-10 rounded-lg border-2 border-gray-300 cursor-pointer"
                                />
                                <input
                                  type="text"
                                  value={qrOptions.color.light}
                                  onChange={(e) =>
                                    setQrOptions((prev) => ({
                                      ...prev,
                                      color: {
                                        ...prev.color,
                                        light: e.target.value,
                                      },
                                    }))
                                  }
                                  className="flex-1 px-3 py-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 text-gray-700"
                                />
                              </div>
                            </div>

                            <div>
                              <label className="block text-sm font-medium text-gray-700 mb-2">
                                Foreground Color
                              </label>
                              <div className="flex items-center space-x-3">
                                <input
                                  type="color"
                                  value={qrOptions.color.dark}
                                  onChange={(e) =>
                                    setQrOptions((prev) => ({
                                      ...prev,
                                      color: {
                                        ...prev.color,
                                        dark: e.target.value,
                                      },
                                    }))
                                  }
                                  className="w-12 h-10 rounded-lg border-2 border-gray-300 cursor-pointer"
                                />
                                <input
                                  type="text"
                                  value={qrOptions.color.dark}
                                  onChange={(e) =>
                                    setQrOptions((prev) => ({
                                      ...prev,
                                      color: {
                                        ...prev.color,
                                        dark: e.target.value,
                                      },
                                    }))
                                  }
                                  className="flex-1 px-3 py-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 text-gray-700"
                                />
                              </div>
                            </div>
                          </div>

                          {/* Margin Control */}
                          <div className="mb-6">
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                              Margin: {qrOptions.margin}
                            </label>
                            <div className="flex items-center space-x-3">
                              <span className="text-sm text-gray-500">0</span>
                              <input
                                type="range"
                                min="0"
                                max="10"
                                step="1"
                                value={qrOptions.margin}
                                onChange={(e) =>
                                  setQrOptions((prev) => ({
                                    ...prev,
                                    margin: parseInt(e.target.value),
                                  }))
                                }
                                className="flex-1 slider-input"
                              />
                              <span className="text-sm text-gray-500">10</span>
                            </div>
                          </div>

                          {/* Error Correction Level */}
                          <div className="mb-6">
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                              Error Correction Level
                            </label>
                            <select
                              value={qrOptions.errorCorrectionLevel}
                              onChange={(e) =>
                                setQrOptions((prev) => ({
                                  ...prev,
                                  errorCorrectionLevel: e.target.value,
                                }))
                              }
                              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 text-gray-700"
                            >
                              <option value="L">Low (7%)</option>
                              <option value="M">Medium (15%)</option>
                              <option value="Q">Quartile (25%)</option>
                              <option value="H">High (30%)</option>
                            </select>
                          </div>

                          {/* Logo Upload */}
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                              Upload Logo
                            </label>
                            <div className="border-2 border-dashed border-gray-300 rounded-lg p-4 text-center hover:border-blue-400 transition-colors">
                              <input
                                type="file"
                                accept="image/*"
                                onChange={handleLogoUpload}
                                className="hidden"
                                id="logo-upload"
                              />
                              <label
                                htmlFor="logo-upload"
                                className="cursor-pointer"
                              >
                                <div className="text-gray-500">
                                  <div className="text-2xl mb-2">📁</div>
                                  <p className="text-sm">
                                    {qrLogo
                                      ? "Logo uploaded!"
                                      : "Click to select file"}
                                  </p>
                                  <p className="text-xs text-gray-400 mt-1">
                                    PNG, JPG up to 2MB
                                  </p>
                                </div>
                              </label>
                            </div>
                            {qrLogo && (
                              <div className="mt-3 flex items-center justify-between p-2 bg-green-50 rounded">
                                <span className="text-sm text-green-700">
                                  ✅ Logo ready
                                </span>
                                <button
                                  onClick={() => setQrLogo(null)}
                                  className="text-red-500 hover:text-red-700 text-sm"
                                >
                                  Remove
                                </button>
                              </div>
                            )}
                          </div>
                        </div>
                      </div>

                      {/* QR Code Preview */}
                      <div className="lg:col-span-2">
                        <div className="bg-white rounded-2xl border border-gray-200 p-6 shadow-sm">
                          <div className="flex items-center justify-between mb-4">
                            <h3 className="text-lg font-semibold text-gray-800">
                              QR Code Preview
                            </h3>
                            <div className="flex items-center space-x-2">
                              <button
                                onClick={() => shareQRCode("facebook")}
                                className="p-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
                                title="Share on Facebook"
                              >
                                📘
                              </button>
                              <button
                                onClick={() => shareQRCode("twitter")}
                                className="p-2 bg-blue-400 text-white rounded hover:bg-blue-500 transition-colors"
                                title="Share on Twitter"
                              >
                                🐦
                              </button>
                              <button
                                onClick={() => shareQRCode("linkedin")}
                                className="p-2 bg-blue-700 text-white rounded hover:bg-blue-800 transition-colors"
                                title="Share on LinkedIn"
                              >
                                💼
                              </button>
                              <button
                                onClick={() => shareQRCode("whatsapp")}
                                className="p-2 bg-green-500 text-white rounded hover:bg-green-600 transition-colors"
                                title="Share on WhatsApp"
                              >
                                💬
                              </button>
                            </div>
                          </div>

                          <div className="qr-preview-container mb-6 bg-gray-50 rounded-2xl p-8 min-h-[320px] flex items-center justify-center">
                            {qrCodeImage ? (
                              <img
                                src={qrCodeImage}
                                alt="Generated QR Code"
                                className="max-w-full h-auto mx-auto rounded-xl shadow-lg"
                                style={{ maxWidth: "300px" }}
                              />
                            ) : (
                              <div className="text-center text-gray-500">
                                <div className="text-6xl mb-4">📱</div>
                                <p className="text-lg font-medium">
                                  Enter URL or text above
                                </p>
                                <p className="text-sm mt-2 opacity-75">
                                  QR code preview will appear here
                                </p>
                              </div>
                            )}
                          </div>

                          {/* Export Options */}
                          <div className="bg-gray-50 rounded-xl p-4">
                            <div className="flex items-center justify-between mb-4">
                              <h4 className="font-medium text-gray-800">
                                Export Options
                              </h4>
                              <div className="flex items-center space-x-2">
                                <select
                                  value={exportSize}
                                  onChange={(e) => setExportSize(e.target.value)}
                                  className="px-3 py-1 text-sm border border-gray-300 rounded focus:ring-2 focus:ring-blue-500 text-gray-700"
                                >
                                  <option value="64px">64 px</option>
                                  <option value="128px">128 px</option>
                                  <option value="256px">256 px</option>
                                  <option value="512px">512 px</option>
                                  <option value="1024px">1024 px</option>
                                </select>
                                <select
                                  value={exportFormat}
                                  onChange={(e) =>
                                    setExportFormat(e.target.value)
                                  }
                                  className="px-3 py-1 text-sm border border-gray-300 rounded focus:ring-2 focus:ring-blue-500 text-gray-700"
                                >
                                  <option value="PNG">PNG</option>
                                  <option value="SVG">SVG</option>
                                  <option value="PDF">PDF</option>
                                </select>
                              </div>
                            </div>

                            <div className="flex gap-3">
                              <button
                                onClick={downloadQRCode}
                                disabled={!qrCodeImage}
                                className="flex-1 btn-primary disabled:opacity-50 disabled:cursor-not-allowed px-6 py-3 rounded-xl"
                              >
                                📥 Download
                              </button>
                              <button
                                onClick={() => copyToClipboard(qrCodeUrl)}
                                disabled={!qrCodeUrl}
                                className="flex-1 btn-secondary disabled:opacity-50 disabled:cursor-not-allowed px-6 py-3 rounded-xl"
                              >
                                📋 Copy URL
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </>
              )}
            </div>
          </div>

          <p className="text-sm mt-8 opacity-75 max-w-2xl mx-auto">
            By using our service, you agree to Linkmini's{" "}
            <span className="underline cursor-pointer hover:opacity-100">
              Terms of Use
            </span>
            ,{" "}
            <span className="underline cursor-pointer hover:opacity-100">
              Privacy Policy
            </span>{" "}
            and{" "}
            <span className="underline cursor-pointer hover:opacity-100">
              Cookie Policy
            </span>
          </p>
        </div>
      </section>

      {/* Enhanced Toast Container */}
      <ToastContainer
        position="bottom-right"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="colored"
        className="text-sm"
        toastClassName="rounded-lg shadow-lg"
      />
    </div>
  );
};

export default MainApp;
